from beflex.aoa.models import Object
from beflex.aoa.services import applyFilterModel2

query = Object.objects

if parameters.get('id'):
    query = query.filter(id=parameters['id'])
else:
    filterModel2 = parameters['request'].get('filterModel2') if parameters.get('request') else None

    if filterModel2:

        if 'package' in filterModel2:
            from django.db.models import Exists, OuterRef, F, CharField
            from django.db.models.functions import Cast
            from beflex.aos.models import PackageObject

            po = PackageObject.objects.filter(deployment_id=filterModel2['package'])

            query = query.annotate(
                id_str=Cast('id', output_field=CharField())
            ).annotate(
                isInPackage=Exists(po.filter(object_id=OuterRef('id_str')))
            ).filter(isInPackage=True)


            del filterModel2['package']

        query = applyFilterModel2(query, filterModel2)
    else:
        query = query.all()

    query = query.order_by('code')

