def getTasks(params):
    if current_user:
        q = Token.objects.distinct().filter(state=Token.STATE_ACTIVE)
        if params.get('id'):
            q = q.filter(id=params['id'])
        else:
            if getattr(current_user, 'su'):
                actual_user = getattr(current_user, 'su')
            else:
                actual_user = current_user

            q = q.filter(Q(performer__isnull=True) | Q(performer=actual_user.code), candidates__username=actual_user.code)


            #    
            qg = None            
            if len(actual_user.groups)>0:
                qg = Token.objects.filter(state=Token.STATE_ACTIVE, performer__isnull=True, candidates__groupname__in=actual_user.groups)

            if 'deployment' in params:
                q = q.filter(deployment__code=params['deployment'])

            order_by = []
            request = params.get('request')
            if request:
                filterModel = request.get('filterModel')
                if filterModel:

                    kwargs = {}
                    for fname, fmodel in filterModel.items():
                        lookupField = fname
                        if lookupField=='name':
                            lookupField = 'description'
                        if fmodel['filterType'] == 'set' and fmodel['values']:
                            kwargs[lookupField + '__in'] = fmodel['values']
                        elif fmodel.get('type') == 'contains':
                            kwargs[lookupField + '__contains'] = fmodel['filter']
                    q = q.filter(**kwargs)

                    if qg:
                        qg = qg.filter(**kwargs)

                filterModel2 = request.get('filterModel2')
                if filterModel2:
                    from beflex.aoa.services import applyFilterModel2
                    q = applyFilterModel2(q, filterModel2)
                    if qg:
                        qg = applyFilterModel2(qg, filterModel2)

                for col in request.get('sortModel', []):
                    field = '-' if col['sort'] == 'desc' else ''
                    col_id = col['colId']
                    if col_id == 'name':
                        col_id = 'description'
                    field += col_id
                    order_by.append(field)                

            if qg:
                q = q.union(qg, all=False)



            if order_by:
                q = q.order_by(*order_by)
            else:
                q = q.order_by('-created')


        if params.get('request', {}).get('startRow') is not None:
            q = q[params['request']['startRow']:params['request']['endRow']]

        #print ('sql: ', q.query)
    else:
        raise Exception('No authenticated user')
    return q


