    r = {
        'id'        : t.id,
        'name'      : taskName,
        'created'   : timezone.localtime(t.created),
        'state'     : t.state,
        'priority'  : t.priority,
        'initiator' : t.initiator,
        'flow': t.flow_id,                    
        'performer' : t.performer
    }
    if t.task_state:
        r['task_state'] = t.task_state
    if t.started:
        r['started'] = t.started
    if t.completed:
        r['completed'] = t.completed

    r['performer2'] = ''
