import json
from beflex.auth import getUser
from beflex.intl import translation


from django.core.serializers.json import DjangoJSONEncoder

from beflex.workplace.models import UserWorkplace
from beflex.workplace.models import Menu 
from beflex.workplace.models import Widget

class WorkplaceJSONEncoder(DjangoJSONEncoder):
    def default(self, o):
        if isinstance(o, Menu):

            if hasattr(o, 'name'):
                r = {'m': o.name}
                if hasattr(o, 'call') and o.call:
                    r['t'] = o.call
                if hasattr(o, 'params') and o.params:
                    r['p'] = o.params
                if len(o.children)>0:
                    r['c'] = o.children
                if len(o.parameters)>0:
                    r['parameters'] = o.parameters
                return r
            else:
                r = [c for c in o.children]
                if o.widgets:
                    r.extend([w for w in o.widgets])
                return r

        elif isinstance(o, Widget):
            return {'w': o.name}    
        else:
            return super().default(o)

def translateMenu(wb, menu):
    menu.name = wb['gettext'](menu.name)
    for m in menu.children:
        translateMenu(wb, m)
    return menu

user_workplaces = UserWorkplace.objects.filter(username=getUser().code)

menu = Menu()
toolbar = []
for uw in user_workplaces:
    wb = translation().bindings('workplace', uw.workplace.code)

    if uw.workplace.data.startswith('<workplace'):
        # Deprecated
        translateMenu(wb, uw.workplace.menu)
    else:
        jdata = json.loads(uw.workplace.data)
        translateMenu(wb, uw.workplace.menu)
        toolbar.extend(jdata.get('toolbar', []))

    menu.merge(uw.workplace.menu)

data = {
    'menu': json.loads(json.dumps(menu, cls=WorkplaceJSONEncoder)),
    'toolbar': toolbar
}

