def item_dict(item):
    parameters = [{'id': str(id(x)), **x} for x in item.parameters]
    res = {'name': item.name, 'parameters': parameters}
    if item.call:
        res['call'] = item.call
    if item.params:
        res['params'] = item.params
    return res


def prepare_menu(items, tree, path_hi):
    for i, item in enumerate(items, 1):
        id_item = str(id(item))
        path = path_hi + [id_item]
        d = {**item_dict(item), 'id': id_item, 'npp': i * 2, 'path': path}
        tree.append(d)
        if item.children:
            prepare_menu(item.children, tree, path)



