from beflex.workplace.models import Workplace
from lxml import etree as ET


if parameters.get('id'):
    workplace = Workplace.objects.get(id=parameters['id'])
else:
    workplace = Workplace()

root = ET.Element('workplace', {
    'code': parameters.get('code'), 
    'name': parameters.get('name')
})

def add_menu_item(item, parent):
    me = ET.Element('menu', {
        'name': item.get('name'),
    })
    if item.get('call'):
        me.set('call', item['call'])

    # параметры меню
    for p in item.get('parameters', []):
        pe = ET.Element('p', {
            'name': p.get('name'),
        })
        if p.get('value'):
            pe.set('value', p['value'])

        me.append(pe)

    # Дочерние элементы
    pk = ','.join(item['path'])
    for m in filter(lambda x: pk==','.join(x['path'][:len(item['path'])]) and len(x['path'])-len(item['path'])==1,parameters['menu']):
        add_menu_item(m, me)

    parent.append(me)
    return me


# По корневым элементам
for m in filter(lambda x: len(x['path'])==1,parameters['menu']):
    add_menu_item(m, root)

wdata = {
    'menu': ET.tostring(root, pretty_print=True, encoding='unicode'),
    'toolbar': parameters.get('toolbar', [])
}

workplace.data = json.dumps(wdata)

#raise UserException(workplace.data)

workplace.save()

data = {
    'id': workplace.id.__str__()
}
